package com.yywf.aly;import android.text.TextUtils;/** * 解析快捷支付结果 * <p>用法：首先PayResult payResult = new PayResult(String result); 再调用该对象的方法,如：payResult.getMemo();</p> * @author Ivan * */public class PayResult {		/**	 * 判断resultStatus 为“9000”则代表支付成功，具体状态码代表含义可参考接口文档	 */	public static final String PAY_SUCCESS = "9000";		/**	 * 判断resultStatus 为"8000"代表支付结果因为支付渠道原因或者系统原因还在等待支付结果确认，最终交易是否成功以服务端异步通知为准（小概率状态）	 */	public static final String PAY_WAITING_CONFIRM = "8000";		private String resultStatus;	private String result;	private String memo;	public PayResult(String rawResult) {		if (TextUtils.isEmpty(rawResult))			return;		String[] resultParams = rawResult.split(";");		for (String resultParam : resultParams) {			if (resultParam.startsWith("resultStatus")) {				resultStatus = gatValue(resultParam, "resultStatus");			}			if (resultParam.startsWith("result")) {				result = gatValue(resultParam, "result");			}			if (resultParam.startsWith("memo")) {				memo = gatValue(resultParam, "memo");			}		}	}	@Override	public String toString() {		return "resultStatus={" + resultStatus + "};memo={" + memo				+ "};result={" + result + "}";	}	private String gatValue(String content, String key) {		String prefix = key + "={";		return content.substring(content.indexOf(prefix) + prefix.length(),				content.lastIndexOf("}"));	}	/**	 * @return the resultStatus	 */	public String getResultStatus() {		return resultStatus;	}	/**	 * @return the memo	 */	public String getMemo() {		return memo;	}	/**	 * @return the result	 */	public String getResult() {		return result;	}}